const Diketik=e=>({el:e,sleep:e=>new Promise(i=>setTimeout(i,e)),isIterable:e=>null!=e&&"function"==typeof e[Symbol.iterator],ticker:async(i,l=250,n=!1)=>{if(null!=e){if(1==n&&(e.innerHTML=""),"string"==typeof i&&i.length>0){i=i.split("");for(var t=0;t<i.length;t++)e.insertAdjacentHTML("beforeend",i[t]),await Diketik(null).sleep(l)}}else console.warn("Diketik.ticker element not found !")},rolling:async(i,l=250)=>{if(null!=e)if(Diketik(0).isIterable(i))for(var n=0;n<i.length;n++)e.innerHTML=i[n],await Diketik(null).sleep(l);else console.log("Diketik.rolling(value) is not iterable.");else console.warn("Diketik.rolling element not found !")}});